'FUNCTION render',
'(',
'	p_dynamic_action IN APEX_PLUGIN.T_DYNAMIC_ACTION,',
'	p_plugin         IN APEX_PLUGIN.T_PLUGIN',
')	RETURN APEX_PLUGIN.T_DYNAMIC_ACTION_RENDER_RESULT ',
'IS',
'    l_result      				APEX_PLUGIN.T_DYNAMIC_ACTION_RENDER_RESULT;',
'    l_ajax_id                   VARCHAR2(4000) := apex_plugin.get_ajax_identifier;',
'    l_update_query              p_dynamic_action.attribute_01%type := p_dynamic_action.attribute_01;',
'    l_seq                       p_dynamic_action.attribute_02%type := p_dynamic_action.attribute_02;',
'    l_seq_query                 p_dynamic_action.attribute_03%type := p_dynamic_action.attribute_03;',
'    l_css_classes               p_dynamic_action.attribute_04%type := p_dynamic_action.attribute_04;',
'BEGIN',
'    ',
'    apex_plugin_util.debug_dynamic_action',
'	(',
'		p_plugin         => p_plugin,',
'		p_dynamic_action => p_dynamic_action',
'	);',
'    ',
'    apex_javascript.add_library',
'	(',
'		p_name                  => ''dragndrop.min'',',
'		p_directory             => p_plugin.file_prefix,',
'		p_check_to_add_minified => false',
'	);',
'',
'    apex_javascript.add_library',
'	(',
'		p_name                  => ''lodash.min'',',
'		p_directory             => p_plugin.file_prefix,',
'		p_check_to_add_minified => false',
'	);',
'',
'    apex_javascript.add_library',
'	(',
'		p_name                  => ''sortable.min'',',
'		p_directory             => p_plugin.file_prefix,',
'		p_check_to_add_minified => false',
'	);',
'    ',
'    apex_css.add_file ',
'      ( p_name           => ''style.css''',
'      , p_directory      => p_plugin.file_prefix',
'      , p_skip_extension => true',
'      ); ',
'',
'    -- all done, return l_result now containing the javascript function',
'    L_RESULT.JAVASCRIPT_FUNCTION := ''function(){dragNDrop.init(this,''||l_css_classes||'');}'';',
'    L_RESULT.ATTRIBUTE_01 := l_update_query;',
'    L_RESULT.ATTRIBUTE_02 := l_ajax_id;',
'    L_RESULT.ATTRIBUTE_03 := l_seq;',
'    L_RESULT.ATTRIBUTE_04 := l_seq_query;',
'    return l_result;',
'end render;',
'',
'function ajax',
'  ( p_dynamic_action in apex_plugin.t_dynamic_action',
'  , p_plugin         in apex_plugin.t_plugin',
'  )',
'return apex_plugin.t_dynamic_action_ajax_result',
'as',
'    l_result            apex_plugin.t_dynamic_action_ajax_result;',
'    l_update_query      p_dynamic_action.attribute_01%type := p_dynamic_action.attribute_01;',
'    l_seq_query         p_dynamic_action.attribute_03%type := p_dynamic_action.attribute_03;',
'    l_sql_parameters    apex_exec.t_parameters;',
'    l_out_value         VARCHAR2(32767);',
'begin',
'    IF apex_application.g_x02 = ''SEQ''',
'    THEN',
'        apex_exec.add_parameter( l_sql_parameters, ''DATA'',  apex_application.g_x01 );',
'        apex_exec.execute_plsql',
'          ( p_plsql_code      => q''#DECLARE',
'                                        TYPE rec_type IS RECORD (',
'                                            id  NUMBER,',
'                                            seq NUMBER);',
'                                        TYPE tbl_type IS TABLE OF rec_type',
'                                        INDEX BY PLS_INTEGER;',
'                                        lt_data tbl_type;',
'                                    BEGIN',
'                                        SELECT id,',
'                                               seq',
'                                        BULK COLLECT INTO lt_data',
'                                        FROM JSON_TABLE (',
'                                                :DATA,',
'                                                ''$[*]'' COLUMNS (',
'                                                        id path ''$.id'',',
'                                                        seq path ''$.seq''));',
'                                        FORALL i IN 1 .. lt_data.COUNT',
'                                        #''||replace(replace(l_seq_query,'':SEQ'',''lt_data(i).seq''),'':ID'',''lt_data(i).id'')||q''#',
'                                        COMMIT;',
'                                    END;#''',
'          , p_auto_bind_items => true',
'          , p_sql_parameters  => l_sql_parameters',
'          );',
'    ELSE',
'        apex_exec.add_parameter( l_sql_parameters, ''ID'',  apex_application.g_x01 );',
'        apex_exec.execute_plsql',
'          ( p_plsql_code      => l_update_query',
'          , p_auto_bind_items => true',
'          , p_sql_parameters  => l_sql_parameters',
'          );',
'    END IF;',
'      ',
'    -- prepare a json object as response for the client',
'    apex_json.initialize_output;',
'    apex_json.open_object;',
'    apex_json.write(''status'', ''success'');',
'    apex_json.close_object;',
'',
'    return l_result;',
'end ajax;'))
